using AutoMapper;
using Sistema.Bico.Domain.Command;
using Sistema.Bico.Domain.Command.Fornecedor;
using Sistema.Bico.Domain.Command.Menu;
using Sistema.Bico.Domain.Command.Produto;
using Sistema.Bico.Domain.Command.Venda;
using Sistema.Bico.Domain.Entities;
using Sistema.Bico.Domain.Generics.Extensions;
using SistemaBico.API.Dtos;
using SistemaBico.API.Util;
using System;

namespace SistemaBico.API.Configurations
{
    public class AutoMapperInterface : Profile
    {
        private string IMAGEM_PADRAO = "";
        public AutoMapperInterface()
        {
            _ = CreateMap<Produto, ProdutoDto>()
                .ForMember(dest => dest.TipoProduto, opt => opt.MapFrom(src => src.TipoProduto != null ? src.TipoProduto.GetDescription() : null))
                .ForMember(dst => dst.FotoBase64, map => map.MapFrom(src => src.FotoBase64.Length > 0 ? Convert.ToBase64String(src.FotoBase64) : IMAGEM_PADRAO))
                .ForMember(dest => dest.DataCadastro, opt => opt.MapFrom(src => src.Created.ToString("dd/MM/yyyy")))
                .ForMember(dest => dest.FornecedorName, opt => opt.MapFrom(src => src.Fornecedor.Nome))
                .ForMember(dest => dest.MenuName, opt => opt.MapFrom(src => src.Menu.Nome))
                .ForMember(dest => dest.ProdutoId, opt => opt.MapFrom(src => src.Id))
                .ForMember(dest => dest.QuantidadeString, opt => opt.MapFrom(src => src.Quantidade.ToString().Replace(",", ".")));

            _ = CreateMap<Venda, VendaDto>()
                .ForMember(dest => dest.DataVenda, opt => opt.MapFrom(src => src.Created.ToString("dd/MM/yyyy")));


            _ = CreateMap<ProdutoDto, AddProdutoCommand>()
                .ForMember(dst => dst.FotoBase64, map => map.MapFrom(src => src.File != null ? ConvertGeneric.IFormFileToBase64(src.File) : null))
                .ForMember(dst => dst.TipoProduto, map => map.MapFrom(src => src.Tipo))
                .ForMember(dst => dst.FornecedorId, map => map.MapFrom(src => Guid.Parse(src.FornecedorId)))
                .ForMember(dst => dst.MenuId, map => map.MapFrom(src => Guid.Parse(src.MenuId)));

            _ = CreateMap<ProdutoDto, EditarProdutoCommand>()
                .ForMember(dst => dst.FotoBase64, map => map.MapFrom(src => src.File != null ? ConvertGeneric.IFormFileToBase64(src.File) : null))
                .ForMember(dst => dst.TipoProduto, map => map.MapFrom(src => src.Tipo))
                .ForMember(dst => dst.Id, map => map.MapFrom(src => src.ProdutoId))
                .ForMember(dst => dst.FornecedorId, map => map.MapFrom(src => Guid.Parse(src.FornecedorId)))
                .ForMember(dst => dst.MenuId, map => map.MapFrom(src => Guid.Parse(src.MenuId)));

            _ = CreateMap<Fornecedor, FornecedorDto>();

            _ = CreateMap<FornecedorDto, AddFornecedorCommand>();
            _ = CreateMap<FornecedorDto, EditarFornecedorCommand>()
              .ForMember(dst => dst.Id, map => map.MapFrom(src => Guid.Parse(src.Id)));

            _ = CreateMap<VendaDto, RegistrarVendaCommand>()
                .ForMember(dst => dst.ProdutoId, map => map.MapFrom(src => Guid.Parse(src.ProdutoId)))
                .ForMember(dst => dst.ValorVendaUnitario, map => map.MapFrom(src => Decimal.Parse(src.ValorVendaUnitario)));

            _ = CreateMap<MenuDto, EditarMenuCommand>();

            _ = CreateMap<Menu, MenuDto>();
            _ = CreateMap<MenuDto, AddMenuCommand>();
        }
    }
}
